<?phprequire("functions.php");$request = $_POST['request'];$key = getKey();/*1. Encrypt request with key  2.hashes key  3.Send to Main URL*/$encrypted = encrypt($request, $key);$hashedKey = md5hash($key);$mainURL = "http://localhost/main/request.php" . "?request=" . $encrypted . "&key=".$hashedKey;$encryptedForm = requestFormFromMain($mainURL);$decryptedForm = decrypt($key, $encryptedForm);saveForm($decryptedForm);function saveForm($decryptedForm){	$myFile = "../vote/index.php";	$fh = fopen($myFile, 'w');	fwrite($fh, $decryptedForm);	fclose($fh);}?>